var interactions = {
	'farine': {
		'doseur': function(farine, doseur) {
			if (doseur.état == 'vide') {
				farine.état='entamé'; 
				doseur.état='plein'; 
				doseur.contenu.push('250g de farine');
				return "250g de farine ont été versés dans le verre doseur";
			}
			throw "interaction impossible";
		},
		'saladier': function(farine, saladier) {
			farine.état='entamé'; 
			saladier.état='blanc';
			saladier.contenu.push('de la farine')
			return "De la farine a été ajoutée dans le saladier";
		}
	},
	'doseur': {
		'saladier': function(doseur, saladier) {
			saladier.contenu=saladier.contenu.concat(doseur.contenu);
			saladier.état = 'blanc'; 
			doseur.contenu = [];
			doseur.état='vide';
			return "Le contenu du doseur a été versé dans le saladier"
		}
	},
	'oeuf': {
		'saladier': function(oeuf, saladier) {
			if (oeuf.état == 'frais' && (['blanc','oeuf'].indexOf(saladier.état)!=-1)) {
				saladier.contenu.push('un oeuf');
				saladier.état = 'oeuf';
				oeuf.état = 'cassé';
				return "Un oeuf a été cassé dans le saladier";
			}
			throw "interaction impossible"
		},
		'doseur': function(oeuf, doseur) {
			return "On ne peut pas mesurer un oeuf !"
		}
	},
};